import React, { useState } from 'react'

const Plane = () => {
    const [destination, setDestination] = useState();
    const [departure, setDeparture] = useState();
    const [planeType, setPlaneID] = useState();

    async function getPlaneID() {
        console.log(planeType)
        const url = 'http://127.0.0.1:3000/api/adminstrator/planes/'+planeType
        const response = await fetch(url);
        const json = await response.json();
        return json.data.id;
    }

    const handleSubmit = async e => {
        e.preventDefault();
        console.log(planeType)
        const planeID = await getPlaneID()
        console.log(planeID)
        fetch('http://127.0.0.1:3000/api/adminstrator/flights', { 
            method: 'POST',
            body: {
              destination: destination,
              departure: departure,
              plane_id: planeID
            }
          })
          .then(function(response) {
            return response.json()
          }).then(function(body) {
            console.log(body);
          });
    }
    return (
        <div className="container">
        <form onSubmit={handleSubmit}>
            <div className="form-control">
                <label>Departure</label>
                <input type='text' placeholder='e.x. Cairo...' onChange={e => setDeparture(e.target.value)}/>
            </div>
            <div className="form-control">
                <label>Destination</label>
                <input type='text' placeholder='e.x. Paris...' onChange={e => setDestination(e.target.value)}/>
            </div>
            <div>
                <select defaultValue="A380" onChange={e => setPlaneID(e.target.value)}>
                    <option value="A380">A380</option>
                    <option value="test">test</option>
                    <option value="B780">B780</option>
                </select>
            </div>
            <div>
            <button type="submit" className='btn'>Submit</button>
            </div>
        </form>
        </div>
    )
}

export default Plane
