import React, { useState } from 'react'

const Plane = () => {
    const [destination, setDestination] = useState();
    const [departure, setDeparture] = useState();
    const [planeType, setPlaneID] = useState();

    const handleSubmit = async (event) => {
        //alert('A list was submitted: ' + this.state.formvalue);
        event.preventDefault();
        setPlaneID(parseInt(event.target.plane.value))
        setDeparture(event.target.departure.value)
        setDestination(event.target.destination.value)
        console.log(destination)
        console.log(departure)
        console.log(planeType)
        fetch('http://127.0.0.1:3000/api/adminstrator/flights', {
          method: 'POST',
          headers: {
            'Accept': 'application/json',
            'Content-Type': 'application/json'
          },
          body: JSON.stringify({
            destination: destination,
            departure: departure,
            plane_id: planeType
          })
        })
        .then(res => res.json())
        .then(data => console.log(data))
        .catch(err => console.log(err));
      }

    return (
        <div className="container">
        <form onSubmit={handleSubmit}>
            <div className="form-control">
                <label>Departure</label>
                <input type='text' name="departure" placeholder='e.x. Cairo...'/>
            </div>
            <div className="form-control">
                <label>Destination</label>
                <input type='text' name="destination" placeholder='e.x. Paris...'/>
            </div>
            <div>
                <select defaultValue="1" name="plane">
                    <option value="1">A380</option>
                    <option value="2">test</option>
                    <option value="6">B780</option>
                </select>
            </div>
            <div>
            <button type="submit" className='btn'>Submit</button>
            </div>
        </form>
        </div>
    )
}

export default Plane
