import React, { useState } from 'react'

const Plane = () => {
    const [destination, setDestination] = useState();
    const [departure, setDeparture] = useState();
    const [planeType, setPlaneID] = useState();

    async function getPlaneID() {
        console.log(planeType)
        const url = 'http://127.0.0.1:3000/api/adminstrator/planes/'+planeType
        const response = await fetch(url).then(response.json())

        return response.data.id;
    }
    const handleSubmit =  async (event) => {
        event.preventDefault()
        setPlaneID(event.target.plane.value)
        setDeparture(event.target.departure.value)
        setDestination(event.target.destination.value)
        const planeID = await getPlaneID()

      }

    return (
        <div className="container">
        <form onSubmit={handleSubmit}>
            <div className="form-control">
                <label>Departure</label>
                <input type='text' name="departure" placeholder='e.x. Cairo...'/>
            </div>
            <div className="form-control">
                <label>Destination</label>
                <input type='text' name="destination" placeholder='e.x. Paris...'/>
            </div>
            <div>
                <select defaultValue="A380" name="plane">
                    <option value="A380">A380</option>
                    <option value="test">test</option>
                    <option value="B780">B780</option>
                </select>
            </div>
            <div>
            <button type="submit" className='btn'>Submit</button>
            </div>
        </form>
        </div>
    )
}

export default Plane
